import{_ as s,c as a,o as e,a as n}from"./app.001f7d66.js";const g=JSON.parse('{"title":"gh repo sync","description":"","frontmatter":{},"headers":[{"level":2,"title":"选项","slug":"选项","link":"#选项","children":[]},{"level":2,"title":"示例","slug":"示例","link":"#示例","children":[]},{"level":2,"title":"另请参见","slug":"另请参见","link":"#另请参见","children":[]}],"relativePath":"gh_repo_sync.md","lastUpdated":1675236702000}'),p={name:"gh_repo_sync.md"},l=n(`<h1 id="gh-repo-sync" tabindex="-1">gh repo sync <a class="header-anchor" href="#gh-repo-sync" aria-hidden="true">#</a></h1><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">gh repo sync [&lt;destination-repository&gt;] [flags]</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>从源仓库同步目标仓库。同步（sync）使用源仓库的主分支来更新目标仓库上的匹配分支，使它们相等。除非指定了 <code>--force</code> 标志，否则将使用快进更新（fast forward update），然后将使用硬重置（hard reset）同步两个分支。</p><p>如果没有参数，则选择本地仓库作为目标仓库。</p><p>默认情况下，源仓库是目标仓库的父级。这可以用 <code>--source</code> 标志覆盖。</p><h2 id="选项" tabindex="-1">选项 <a class="header-anchor" href="#选项" aria-hidden="true">#</a></h2><p><code>-b</code>, <code>--branch &lt;string&gt;</code></p><p>要同步的分支（默认：main branch）</p><p><code>--force</code></p><p>硬重置目标仓库的分支以匹配源仓库</p><p><code>-s</code>, <code>--source &lt;string&gt;</code></p><p>源仓库</p><h2 id="示例" tabindex="-1">示例 <a class="header-anchor" href="#示例" aria-hidden="true">#</a></h2><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;"># 从远程父级（remote parent）同步本地仓库</span></span>
<span class="line"><span style="color:#A6ACCD;">$ gh repo sync</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"># 从特定分支上的远程父级同步本地仓库</span></span>
<span class="line"><span style="color:#A6ACCD;">$ gh repo sync --branch v1</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"># 从其父级同步远程复刻</span></span>
<span class="line"><span style="color:#A6ACCD;">$ gh repo sync owner/cli-fork</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"># 从另一个远程仓库同步远程仓库</span></span>
<span class="line"><span style="color:#A6ACCD;">$ gh repo sync owner/repo --source owner2/repo2</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="另请参见" tabindex="-1">另请参见 <a class="header-anchor" href="#另请参见" aria-hidden="true">#</a></h2><ul><li><a href="/github-cli-manual-zh/gh_repo.html">gh repo</a></li></ul>`,16),o=[l];function c(r,t,i,d,h,C){return e(),a("div",null,o)}const y=s(p,[["render",c]]);export{g as __pageData,y as default};
