import{_ as s,c as e,o as p,a}from"./app.888d8daa.js";const C=JSON.parse('{"title":"gh search repos","description":"","frontmatter":{},"headers":[{"level":2,"title":"选项","slug":"选项","link":"#选项","children":[]},{"level":2,"title":"示例","slug":"示例","link":"#示例","children":[]},{"level":2,"title":"另请参见","slug":"另请参见","link":"#另请参见","children":[]}],"relativePath":"gh_search_repos.md","lastUpdated":1675236702000}'),n={name:"gh_search_repos.md"},t=a(`<h1 id="gh-search-repos" tabindex="-1">gh search repos <a class="header-anchor" href="#gh-search-repos" aria-hidden="true">#</a></h1><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">gh search repos [&lt;query&gt;] [flags]</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>在 GitHub 上搜索仓库。</p><p>该命令支持使用 GitHub 搜索语法、使用参数和限定符标志或两者的组合来构造查询。</p><p>GitHub 搜索语法记录在：<a href="https://docs.github.com/search-github/searching-on-github/searching-issues-and-pull-requests" target="_blank" rel="noreferrer">https://docs.github.com/search-github/searching-on-github/searching-issues-and-pull-requests</a></p><h2 id="选项" tabindex="-1">选项 <a class="header-anchor" href="#选项" aria-hidden="true">#</a></h2><p><code>--archived</code></p><p>根据存档状态进行筛选</p><p><code>--created &lt;date&gt;</code></p><p>基于创建日期进行筛选</p><p><code>--followers &lt;number&gt;</code></p><p>根据关注者数量进行筛选</p><p><code>--forks &lt;number&gt;</code></p><p>根据 fork 数量过滤</p><p><code>--good-first-issues &lt;number&gt;</code></p><p>按带有“good first issue”标签的议题数量进行筛选</p><p><code>--help-wanted-issues &lt;number&gt;</code></p><p>按带有“help wanted”标签的议题数量进行筛选</p><p><code>--include-forks &lt;string&gt;</code></p><p>在获取的仓库中包含 fork：{false|true|only}</p><p><code>-q</code>, <code>--jq &lt;expression&gt;</code></p><p>使用 jq 表达式筛选 JSON 输出</p><p><code>--json &lt;fields&gt;</code></p><p>使用指定字段输出 JSON</p><p><code>--language &lt;string&gt;</code></p><p>基于编程语言的筛选</p><p><code>--license &lt;strings&gt;</code></p><p>基于许可证类型进行筛选</p><p><code>-L</code>, <code>--limit &lt;int&gt;</code></p><p>要获取的最大仓库数量</p><p><code>--match &lt;strings&gt;</code></p><p>将搜索限制为仓库的特定字段：{name|description|readme}</p><p><code>--number-topics &lt;number&gt;</code></p><p>筛选主题数量</p><p><code>--order &lt;string&gt;</code></p><p>返回的仓库顺序，除非指定了“--sort”标志，否则将被忽略：{asc|desc}</p><p><code>--owner &lt;string&gt;</code></p><p>按所有者筛选</p><p><code>--size &lt;string&gt;</code></p><p>按大小范围进行筛选（以 KB 为单位）</p><p><code>--sort &lt;string&gt;</code></p><p>对获取的仓库进行排序：{forks|help-wanted-issues|stars|updated}</p><p><code>--stars &lt;number&gt;</code></p><p>根据 star 数量筛选</p><p><code>-t</code>, <code>--template &lt;string&gt;</code></p><p>使用 Go 模板格式化 JSON 输出；请参阅“gh help formatting”</p><p><code>--topic &lt;strings&gt;</code></p><p>按主题筛选</p><p><code>--updated &lt;date&gt;</code></p><p>筛选上次更新日期</p><p><code>--visibility &lt;strings&gt;</code></p><p>基于可见性的筛选：{public|private|internal}</p><p><code>-w</code>, <code>--web</code></p><p>在 Web 浏览器中打开搜索查询</p><h2 id="示例" tabindex="-1">示例 <a class="header-anchor" href="#示例" aria-hidden="true">#</a></h2><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;"># 搜索仓库匹配关键词“cli”和“shell”</span></span>
<span class="line"><span style="color:#A6ACCD;">$ gh search repos cli shell</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"># 搜索仓库匹配短语“vim plugin”</span></span>
<span class="line"><span style="color:#A6ACCD;">$ gh search repos &quot;vim plugin&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"># 搜索在 microsoft 组织中的公共仓库</span></span>
<span class="line"><span style="color:#A6ACCD;">$ gh search repos --owner=microsoft --visibility=public</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"># 按主题搜索仓库</span></span>
<span class="line"><span style="color:#A6ACCD;">$ gh search repos --topic=unix,terminal</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"># 按编程语言和“good first issues”的数量搜索仓库</span></span>
<span class="line"><span style="color:#A6ACCD;">$ gh search repos --language=go --good-first-issues=&quot;&gt;=10&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"># 搜索不带“linux”主题的仓库</span></span>
<span class="line"><span style="color:#A6ACCD;">$ gh search repos -- -topic:linux</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="另请参见" tabindex="-1">另请参见 <a class="header-anchor" href="#另请参见" aria-hidden="true">#</a></h2><ul><li><a href="/github-cli-manual-zh/gh_search.html">gh search</a></li></ul>`,58),l=[t];function o(c,r,i,d,h,g){return p(),e("div",null,l)}const A=s(n,[["render",o]]);export{C as __pageData,A as default};
